mapper.select:
SELECT login_id AS id, pwd, otp_code, otp_date, user_no, pwd_fail_cnt, lock_start_time, last_login_date
FROM employee 
WHERE login_id = #{id}

mapper.update:
UPDATE employee
SET last_login_date = NOW()
WHERE user_no = #{userNo}

UPDATE employee
SET otp_code = #{otpCode}, otp_date = #{otpDate}
WHERE user_no = #{userNo}
